import { createSlice } from "@reduxjs/toolkit";
import { ProductData } from "../component/ProductData";

const initialState = {
  cart: [],
  items: ProductData,
  totalQuantity: 0,
  totalPrice: 0,
};

export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducer: {},
});

export default initialState.reducer;
